import React from "react";
import { Redirect } from 'react-router-dom'
import { connect } from 'react-redux'
import { setUser } from './redux/actions/user_actions'

class LogIn extends React.Component{
      handleSubmit = (e) => {
        e.preventDefault()
        fetch("http://localhost:3000/api/v1/login", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "Accept": "application/json"
          },
          body: JSON.stringify(this.state)
        })
        .then(res => res.json())
        .then(response => {
          if (response.errors){
            alert(response.errors)
          } else {
            this.props.setUser(response)
            this.setState({
                username: "",
                password: ""
            })

          }
        })
    }

    render(){
        if (this.props.currentUser !== null) {
            return <Redirect to='/' />;
        } 
        return (
        <div className="login-main">
            <p className="sign" align="center" >Log In</p>
            <form className="form1" onSubmit={this.handleSubmit}>
                <input className="un" type="text" align="center" name="username"placeholder="Username" />
                <input className="pass" type="password" align="center" name="password"placeholder="Password" />
                <input className="submit" align="center" type="submit" value="Log In" />
            </form>
        </div>
        );
    }
}

function msp(state){
  return {
    currentUser: state.userReducer.currentUser
  }
}

export default connect(msp, { setUser })(LogIn)
