import React from "react";
import MaskedInput from 'react-maskedinput'
import DateTimePicker from 'react-datetime-picker'
import { Redirect } from 'react-router-dom'
import { connect } from 'react-redux'
import { setUser } from './redux/actions/user_actions'

class SignUp extends React.Component{
    state = {
        name: "",
        event_pic: null,
        price: "",
        datetime: "",
        location: "",
        players_per_team: "",
        max_num_of_teams: "",
        description: "",
        loaded: false
    }

    handleChange = (event) => {
        this.setState({
          [event.target.name]: event.target.value
        })
    }

    addPic = (e) => {
        // console.log(e.target.files)
        this.setState({
            event_pic: e.target.files[0],
        });
    }



    handleSubmit = (e) => {
        e.preventDefault()
        const fD = new FormData()
        fD.append("name", this.state.name)
        fD.append("price", this.state.price)
        fD.append("datetime", this.state.datetime)
        fD.append("event_pic", this.state.event_pic)
        fD.append("location", this.state.location)
        fD.append("players_per_team", this.state.players_per_team)
        fD.append("max_num_of_teams", this.state.max_num_of_teams)
        fD.append("description", this.state.description)
        console.log(this.state.event_pic)
        if (this.state.password === this.state.passwordConfirmation){
            fetch("http://localhost:3000/api/v1/signup", {
            method: "POST",
            body: fD
            })
            .then(res => res.json())
            .then(response => {
                if(response.errors){
                    alert(response.errors)
                } else {
                    let token = response.token
                    fetch("http://localhost:3000/api/v1/user_activities", {
                        method: "POST",
                        headers: {
                            "Content-Type": "application/json",
                            "Accept": "application/json"
                        },
                        body: JSON.stringify({
                            user_id: response.user.data.attributes.id,
                            sports: this.state.activity_ids
                        })
                    })
                    .then(
                        setTimeout(() => {
                            fetch("http://localhost:3000/api/v1/auto_login", {
                                headers: {
                                "authorization": token
                                }
                            })
                            .then(res => res.json())
                            .then(user =>{
                                if (user.errors){
                                alert(user.errors)
                                } else {
                                this.props.setUser(user)
                                localStorage.token = user.token
                                this.setState({
                                    first_name: "",
                                    price: "",
                                    datetime: "",
                                    location: "",
                                    players_per_team: "",
                                    max_num_of_teams: "",
                                    birthday: "",
                                    gender: "",
                                    description: "",
                                    admin: 0,
                                    activity_ids: [],
                                    password: "",
                                    passwordConfirmation: "",
                                    loaded: true
                                })
                                }
                            })
                        }, 1000)
                    )
                }
                })
        } else {
            alert("Passwords don't match!")
        }

    }

    // componentDidMount(){
    // }

    render(){
        return (
        <div className="signup-main">
            <form onSubmit={this.handleSubmit} className='form'>
            <p className='field required '>
                <label className='label required' htmlFor='name'>Name</label>
                <input className='text-input' id='name' name='name' value={this.state.name} onChange={this.handleChange} required type='text'/>
            </p>
            <p className='field required half'>
                <label className='label required' htmlFor='price'>Last name</label>
                <input className='text-input' id='price' name='price' value={this.state.price} onChange={this.handleChange} required type='number'/>
            </p>
            <div className='field required half'>
                <label className='label' htmlFor='datetime'>Time of the Event</label>
                <DateTimePicker
                    style={{border: "0px"}}
                    required={true}
                    className='text-input'
                    disableClock={true}
                    onChange={datetime => this.setState({ datetime })}
                    value={this.state.datetime}
                />
                {/* <input className='text-input' id='datetime' name='datetime' value={this.state.datetime} onChange={this.handleChange} required type='datetime' /> */}
            </div>
            <p className='field required half'>
                <label className='label' htmlFor='players_per_team'>Players Per Team</label>
                <input className='text-input' id='players_per_team' name='players_per_team' value={this.state.players_per_team} onChange={this.handleChange} required type='number' min="2" max="15" />
            </p>
            <p className='field required half'>
                <label className='label' htmlFor='max_num_of_teams'>Max Team Number</label>
                <input className='text-input' id='max_num_of_teams' name='max_num_of_teams' value={this.state.max_num_of_teams} onChange={this.handleChange} type='number' min="2" max="32"/>
            </p>
            <p className='field required'>
                <label className='label' htmlFor='event_pic'>Event Picture</label>
                <input className='text-input' id='event_pic' name='event_pic' onChange={this.addPic} accept="image/*" required type='file'/>
            </p>
            <p className='field'>
                <label className='label' htmlFor='description'>Description</label>
                <textarea className='textarea' cols='50' id='description' name='description' value={this.state.description} onChange={this.handleChange} rows='4'></textarea>
            </p>
            <p className='field'>
                <input className='button' type='submit' value='Sign Up' />
            </p>
            </form>
        </div>
        );
    }
}

function msp(state){
    return {
      currentUser: state.userReducer.currentUser,
      players_per_team: state.userReducer.players_per_team,
      password: state.userReducer.password
    }
}

export default connect(msp, { setUser })(SignUp)
