import React, {Fragment} from "react";
import { Redirect } from 'react-router-dom'
import { connect } from 'react-redux'
import { setUser } from './redux/actions/user_actions'
import { changeViewStatus } from './redux/actions/team_actions'

class GroupCard extends React.Component{

    render(){
        console.log(this.props)
        return (
        <div onClick={this.joinTeam} className="team_card">
            <h1 className="team_name">{this.props.group.name}</h1>
            <p className="team_location">{this.props.group.location}</p>
            <img className="team_logo" src={this.props.group.logo_path} />
            <h2 className="team_desc">{this.props.group.description}</h2>
            {this.props.viewStatus === 1 ?
            <Fragment>
                <button style={{marginTop: "50px"}} className='dash_button'>View Group</button>
                <button style={{marginTop: "50px"}} className='dash_button'>Join Group</button>
            </Fragment>
            :
            null
            }
        </div>
        );
    }
}

function msp(state){
    return {
        currentUser: state.userReducer.currentUser,
        sportId: state.teamReducer.currentSportId,
        viewStatus: state.teamReducer.viewStatus
    }
}

export default connect(msp, { setUser, changeViewStatus })(GroupCard)
// changeViewStatus on all button presses
