  //             logout={this.logout} 
  //             chosenEvent={this.state.showEvent} 
  //             chooseEvent={this.chooseEvent} 
  //             setAllNearesEvents={this.setAllNearesEvents}
  //             setAllChoosenEvents={this.setAllChoosenEvents}
  //             setAllFreeEvents={this.setAllFreeEvents}
