import React, {Fragment} from "react";
import { Redirect } from 'react-router-dom'
import { connect } from 'react-redux'

class Dashboard extends React.Component{
    handleCreateTeam = () => {
        console.log("create")
        this.props.history.push()
    }

    render(){
        console.log(this.props.currentUser)
        return (
            <Fragment>
                <form onSubmit={this.handleSubmit} className='form'>
                <p className='field half'></p>
                <p className='field half'>
                    <select className='select' name="location" id='select'>
                    <option value='' defaultValue disabled>Choose Sport:</option>
                    {this.props.currentUser.user.data.attributes.activities.map(sport => <option key={sport.name} value={sport.name}>{sport.name}</option>)}
                    </select>
                </p> 
                </form>
                {this.props.currentUser.user.data.attributes.team_id?
                <div className="main">  
                    <p>Yo</p>
                </div>
                :
                <div className="dash_main">  
                    <h3 className="main_text">You are not currently</h3>
                    <h3 className="main_text">a member of any team</h3>
                    <h3 className="main_text">but we got you, you can:</h3><br/><br/>
                    <button className='dash_button' onClick={this.handleCreateTeam}>Create a team</button>
                    <button className='dash_button'>Search for a team to join</button>
                </div>
                }
            </Fragment>
        );
    }
}

function msp(state){
    return {
        currentUser: state.userReducer.currentUser
    }
}

export default connect(msp)(Dashboard)
