import React from "react";
import { Redirect } from 'react-router-dom'
import { connect } from 'react-redux'
import { setUser } from './redux/actions/user_actions'

class LogIn extends React.Component{
      handleSubmit = (e) => {
        e.preventDefault()
        fetch("http://localhost:3000/api/v1/login", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "Accept": "application/json"
          },
          body: JSON.stringify(this.state)
        })
        .then(res => res.json())
        .then(response => {
          if (response.errors){
            alert(response.errors)
          } else {
            console.log(response)
            this.props.setUser(response)
            localStorage.token = response.token
          }
        })
    }

    render(){
        if (this.props.currentUser !== null) {
            return <Redirect to='/' />;
        } 
        return (
        <div className="login-main">
          <form onSubmit={this.handleSubmit} className='form'>
          <p className='field required'>
              <label className='label' for='username'>Username</label>
              <input className='text-input' id='username' name='username' required type='username' />
          </p>
          <p className='field required'>
              <label className='label' for='password'>Password</label>
              <input className='text-input' id='password' name='password' required type='password'/>
          </p>
          <p className='field'>
          </p>
          <p className='field'>
              <input className='button' type='submit' value='Log In' />
          </p>
          </form>
        </div>
        );
    }
}

function msp(state){
  return {
    currentUser: state.userReducer.currentUser
  }
}

export default connect(msp, { setUser })(LogIn)
