import React, {Fragment} from "react";
import { Redirect } from 'react-router-dom'
import { connect } from 'react-redux'

class TeamSignUp extends React.Component{
    state = {
        name: "",
        logo_path: "",
        phone_number: "",
        birthday: "",
        gender: "",
        description: "",
        won_games: 0,
        tie_games: 0,
        lost_games: 0,
        admin: 0,
        team_id: null,
        activity_ids: [],
        password: "",
        passwordConfirmation: ""
    }

    handleChange = (event) => {
        this.setState({
          [event.target.name]: event.target.value
        })
    }

    addActivities = (event) => {
        if(this.state.activity_ids.find(el => el === event.target.value)){
            this.setState({
                activity_ids: this.state.activity_ids.filter(el => el !== event.target.value)
            })
        }else{
            this.setState({
                activity_ids: [...this.state.activity_ids, event.target.value]
            })
        }
    }

    handleSubmit = (e) => {
        e.preventDefault()
        console.log(e.target)
        if (this.state.password === this.state.passwordConfirmation){
            fetch("http://localhost:3000/api/v1/signup", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json"
            },
            body: JSON.stringify(this.state)
            })
            .then(res => res.json())
            .then(response => {
            if(response.errors){
                alert(response.errors)
            } else {
                this.props.setUser(response)
                localStorage.token = response.token

                fetch("http://localhost:3000/api/v1/user_activities", {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                        "Accept": "application/json"
                    },
                    body: JSON.stringify({
                        user_id: response.user.data.attributes.id,
                        sports: this.state.activity_ids
                    })
                })
                .then(res => res.json())
                .then(console.log)

                this.setState({
                    first_name: "",
                    last_name: "",
                    username: "",
                    location: "",
                    email: "",
                    phone_number: "",
                    birthday: "",
                    gender: "",
                    bio: "",
                    admin: 0,
                    team_id: null,
                    activity_ids: [],
                    password: "",
                    passwordConfirmation: ""
                })
            }
            })
        } else {
            alert("Passwords don't match!")
        }

    }

    componentDidMount(){
        console.log(this.props)
        if(this.props.email !== "" && this.props.password !== ""){
            this.setState({
                email: this.props.email,
                password: this.props.password,
                passwordConfirmation: this.props.password
            })
        }
    }



    render(){
        return (
        <div className="signup-main">
            <form onSubmit={this.handleSubmit} className='form'>
            <p className='field required half'>
                <label className='label required' htmlFor='name'>Name</label>
                <input className='text-input' id='name' name='name' value={this.state.name} onChange={this.handleChange} required type='text'/>
            </p>
            <p className='field required half'>
                <label className='label' htmlFor='logo_path'>Logo</label>
                <input className='text-input' id='logo_path' name='logo_path' value={this.state.logo_path} onChange={this.handleChange} required type='logo_path' />
            </p>
            <p className='field'>
                <label className='label' htmlFor='description'>Description</label>
                <textarea className='textarea' cols='50' id='description' name='description' value={this.state.description} onChange={this.handleChange} rows='4'></textarea>
            </p>
            <p className='field'>
                <input className='button' type='submit' value='Sign Up' />
            </p>
            </form>
        </div>
        );
    }
}

function msp(state){
    return {
        currentUser: state.userReducer.currentUser,
        sportId: state.teamReducer.currentSportId
    }
}

export default connect(msp)(TeamSignUp)
