import React, {Fragment} from "react";
import { Redirect } from 'react-router-dom'
import { connect } from 'react-redux'
import { passEmailAndPass } from './redux/actions/user_actions'

class Dashboard extends React.Component{
    state={
        redirected: false
    }

    handleSubmit = (e) => {
        e.preventDefault()
        this.props.passEmailAndPass(e.target.querySelectorAll("input")[0].value, e.target.querySelectorAll("input")[1].value)
        this.setState({
            redirected: true
        })
    }

    render(){
        if(this.state.redirected){
            this.setState({
                redirected: false
            })
            return <Redirect to='/signup' />
            //Fix render error!!!
        }
        return (
            <div className="main">   
                <h1>Yo</h1>
            </div>
        );
    }
}

function msp(state){
    return {
      email: state.userReducer.email,
      password: state.userReducer.password
    }
}

export default connect(msp, { passEmailAndPass })(Dashboard)
