import React, {Fragment} from "react";
import { Redirect, NavLink } from 'react-router-dom'
import { connect } from 'react-redux'
import { setGroup } from './redux/actions/team_actions'

class GroupCard extends React.Component{

    state= {
        added: false,
        viewed: false
    }

    handleAddGroup = () => {
        fetch("http://localhost:3000/api/v1/team_groups", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json"
            },
            body: JSON.stringify({
                group_id: this.props.group.id,
                team_id: this.props.myTeam.id
            })
            })
        .then(res => res.json())
        .then(
            this.setState({
                added: true
            })
        )
    }

    handleViewGroup = () => {
        // this.props.setGroup(this.props.group)
        this.setState({
            viewed: true
        })
    }

    render(){
        console.log(this.props.group)
        if(this.state.added){
            this.setState({
                added: false
            })
            return <Redirect to="/dashboard" />
        }
        if(this.state.viewed){
            this.setState({
                viewed: false
            })
            return <Redirect to={`/group/${this.props.group.id}`} />
        }
        return (
            <Fragment>
                {
                    this.props.dash_style?
                    <NavLink to={`/group/${this.props.group.id}`}>
                        <figure style={{backgroundImage: `url(${this.props.group.logo_path})`}}>
                            <figcaption>
                                <h4> <span>{this.props.group.name}</span></h4>
                                {/* <h4> <span>{this.props.group.location}</span></h4> */}
                                <p>{this.props.group.location}</p>
                            </figcaption>
                        </figure>
                    </NavLink>
                    :
                    <div class="container">
                    <div class="card">
                        <h2>North</h2>
                        <i class="fas fa-arrow-right"></i>
                        <p>a lonely trip.</p>
                        <div class="pic"></div>
                        <ul>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        </ul>
                        <div class="social">
                        <i class="fab fa-facebook-f"></i>
                        <i class="fab fa-twitter"></i>
                        <i class="fab fa-instagram"></i>
                        <i class="fab fa-github"></i>
                        </div>
                        <button>
                        </button>
                    </div>
                    </div>
                    // <div onClick={this.props.add === 0 ? this.handleViewGroup : null} className={this.props.dash_style? "dash_square_card" : "regular_card"}>
                    //     <h1 className="group_name">{this.props.group.name}</h1>
                    //     <p className="group_location">{this.props.group.location}</p>
                    //     <img className="group_logo" src={this.props.group.logo_path} alt="group_logo" />
                    //     <h2 className="group_desc">{this.props.group.description}</h2>
                    //     {this.props.add === 1 ?
                    //     <Fragment>
                    //         <button onClick={this.handleViewGroup} style={{marginTop: "50px"}} className='dash_button'>View Group</button>
                    //         <button onClick={this.handleAddGroup} style={{marginTop: "50px"}} className='dash_button'>Join Group</button>
                    //     </Fragment>
                    //     :
                    //     null
                    //     }
                    // </div>
                }
            </Fragment>
        );
    }
}

function msp(state){
    return {
        currentUser: state.userReducer.currentUser,
        myTeam: state.teamReducer.currentTeam,
        sportId: state.teamReducer.currentSportId
    }
}

export default connect(msp, { setGroup })(GroupCard)
