import React from "react";
import { Redirect } from 'react-router-dom'

export default class SignUp extends React.Component{
    handleChange = (event) => {
        this.setState({
          [event.target.name]: event.target.value
        })
      }

    handleSubmit = (e) => {
        e.preventDefault()
        if (this.state.password === this.state.passwordConfirmation){
            fetch("http://localhost:3000/api/v1/signup", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json"
            },
            body: JSON.stringify({

            })
            })
            .then(res => res.json())
            .then(response => {
            if(response.errors){
                alert(response.errors)
            } else {
                this.props.setUser(response)
            }
            })
        } else {
            alert("Passwords don't match!")
        }

    }


    render(){
        if (this.props.currentUser !== null && this.props.currentUser !== undefined) {
            return <Redirect to='/' />;
        } 
        return (
            <div className="signup-main">
            <p className="sign" align="center" >Sign Up</p>
            <form className="form1" onSubmit={this.handleSubmit}>
                <input className="un" type="text" align="center" name="fname" placeholder="First Name" />
                <input className="un" type="text" align="center" name="lname" placeholder="Last Name" />
                <input className="un" type="text" align="center" name="phonenumber" placeholder="Phone Number" />
                <input className="un" type="text" align="center" name="username" placeholder="Username" />
                <input className="pass" type="password" align="center" name="password" placeholder="Password" />
                <input className="pass" type="password" align="center" name="passwordConfirmation" placeholder="Password Confirmation" />
                <input className="submit" align="center" type="submit" value="Sign Up" />
            </form>
        </div>
        );
    }
}
