import React from "react";
import { Redirect } from 'react-router-dom'
import { connect } from 'react-redux'
import { setUser } from './redux/actions/user_actions'

class SignUp extends React.Component{
    handleChange = (event) => {
        this.setState({
          [event.target.name]: event.target.value
        })
      }

    handleSubmit = (e) => {
        e.preventDefault()
        console.log(e.target[0].value)
        if (this.state.password === this.state.passwordConfirmation){
            fetch("http://localhost:3000/api/v1/signup", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json"
            },
            body: JSON.stringify({
                first_name: e.target[0].value,
                last_name: e.target[1].value,
                username: e.target[2].value,
                location: e.target[3].value,
                email: e.target[4].value,
                phone_number: e.target[5].value,
                birthday: e.target[6].value,
                gender: e.target[7].value,
                bio: e.target[8].value,
                won_games: 0,
                tie_games: 0,
                lost_games: 0,
                admin: 0,
                team_id: null,
                password: e.target[9].value,
                passwordConfirmation: e.target[10].value
            })
            })
            .then(res => res.json())
            .then(response => {
            if(response.errors){
                alert(response.errors)
            } else {
                this.props.setUser(response)
                localStorage.token = response.token
            }
            })
        } else {
            alert("Passwords don't match!")
        }

    }

    componentDidMount(){
        function init() {
            // Generate li foreach fieldset
            for (var i = 0; i < count; i++) {
              var ul = document.querySelector('ul.items'),
                  li = document.createElement("li");

              ul.appendChild(li);
            }
            // Add class active on first li
            ul.firstChild.classList.add('active');
          }

          function next(target) {
            var input = target.previousElementSibling;

            // Check if input is empty
            if (input.value === '') {
              body.classList.add('error');
            } else {
              body.classList.remove('error');

              var enable = document.querySelector('form fieldset.enable'),
                  nextEnable = enable.nextElementSibling;
              enable.classList.remove('enable');
              enable.classList.add('disable');
              nextEnable.classList.add('enable');

              // Switch active class on left list
              var active = document.querySelector('ul.items li.active'),
                  nextActive = active.nextElementSibling;
              active.classList.remove('active');
              nextActive.classList.add('active');
            }
          }

          function keyDown(event) {
            var key = event.keyCode,
                target = document.querySelector('fieldset.enable .button');
            if (key == 13 || key == 9) next(target);
          }

          var body = document.querySelector('body'),
              form = document.querySelector('form'),
              count = form.querySelectorAll('fieldset').length;

          window.onload = init;
          document.body.onmouseup = function (event) {
              var target = event.target || event.toElement;
              if (target.classList.contains("button")) next(target);
          };
          document.addEventListener("keydown", keyDown, false);
    }



    render(){
        if (this.props.currentUser !== null && this.props.currentUser !== undefined) {
            return <Redirect to='/' />;
        } 
        return (
            <div className="signup-main">
            <p className="sign" align="center" >Sign Up</p>
            <form className="form1" onSubmit={this.handleSubmit}>
            <ul className="items"></ul>
                <fieldset className="username enable">
                    <div className="icon left" align="left"><i className="user"></i></div>
                    <input type="text" name="username" placeholder="Username"/>
                    <div className="icon right button"><i className="arrow"></i></div>
                </fieldset>
                <fieldset className="email">
                    <div className="icon left"><i className="letter"></i></div>
                    <input type="mail" name="email" placeholder="Email"/>
                    <div className="icon right button"><i className="arrow"></i></div>
                </fieldset>
                <fieldset className="password">
                    <div className="icon left"><i className="lock"></i></div>
                    <input type="password" name="password" placeholder="Password"/>
                    <div className="icon right button"><i className="arrow"></i></div>
                </fieldset>
                <fieldset className="thanks">
                    <div className="icon left"><i className="heart"></i></div>
                    <input type="submit" value="Sign Up" />
                    <div className="icon right"><i className="heart"></i></div>
                </fieldset>
            </form>
        </div>
        );
    }
}

function msp(state){
    return {
      currentUser: state.userReducer.currentUser
    }
}

export default connect(msp, { setUser })(SignUp)
