const defaultState = {
  currentSportId: 0,
  viewStatus: null
  }

  function teamReducer(prevState = defaultState, action){
    switch(action.type){
      case "CHANGE_SPORT_ID":
        return {...prevState, currentSportId: action.payload.currentSportId}
      case "CHANGE_GROUP_VIEWING_STATUS":
          return {...prevState, viewStatus: action.payload.viewStatus}
      default:
        return prevState
    }

  }

  export default teamReducer
