const defaultState = {
  currentSportId: 0,
  currentTeam: null,
  currentGroups: null
  }

  function teamReducer(prevState = defaultState, action){
    switch(action.type){
      case "CHANGE_SPORT_ID":
        return {...prevState, currentSportId: action.payload.currentSportId}
      case "SET_TEAM":
          return {...prevState, currentTeam: action.payload.currentTeam}
      case "SET_GROUPS":
        return {...prevState, currentGroup: action.payload.currentGroups}
      default:
        return prevState
    }

  }

  export default teamReducer
